package corejava;

public class airthmeticOperations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1,num2,add,sub,div,mul,mod;
		
		num1=12;
		num2=10;
		
		//For adding two nos.
		add=num1+num2;
		
		//For subracting two nos.
		sub=num1-num2;
		
		//For dividing two nos.
		div=num1/num2;
		
		//For multiplying two nos.
		mul=num1*num2;
		
		//For modulo division of two nos.
		mod=num1%num2;
		
		System.out.println("Addition: "+add);				
		System.out.println("Subtraction: "+sub);				
		System.out.println("Divivsion: "+div);				
		System.out.println("Multioplication: "+mul);				
		System.out.println("ModuloDivision: "+mod);				
	}

}
